def get_data(
    query: str,  # GraphQL query
    api_url: str = apiUrl,  # Monday.com API url
    headers: dict = headers,  # Monday.com API headers
) -> dict:  # response from Monday.com API
    data = {"query": query}
    return requests.post(url=apiUrl, json=data, headers=headers)

